import { ComputeType } from 'aws-cdk-lib/aws-codebuild';
import { SubnetType } from 'aws-cdk-lib/aws-ec2';
/**
 *
 * @interface
 * @description - CDK Pipeline account configuration properties
 *
 */
export interface Config {
    /**
    *
    * @required - ControlHub VpcId
    *
   */
    readonly vpcId: string;
    /**
     *
     * @optional - type of subnet (default: PRIVATE_WITH_NAT)
     *
    */
    readonly subnetType?: SubnetType;
    /**
     *
     * optional - Image properties
     *
     */
    readonly image?: ImageProps;
    /**
     *
     * @optional - type of compute to use for this build (default: medium)
     *
    */
    readonly computeType?: ComputeType;
    /**
     *
     * @optional - CDK CodeBuildStep synth attributes
     *
    */
    readonly synth?: SynthProps;
}
/**
 *
 * @interface
 * @description - CDK CodeBuildStep synth properties
 *
 */
export interface SynthProps {
    /**
     *
     * @optional - enable deploying to other accounts (default: true)
     *
    */
    readonly crossAccountKeys?: boolean;
    /**
     *
     * @optional - disable self-mutation feature (default: false)
     *
    */
    readonly selfMutation?: boolean;
}
/**
 *
 * @interface
 * @description - Image properties
 *
 */
export interface ImageProps {
    /**
     *
     * @optional - CDK Pipeline Repository (default: continuous-integration)
     *
    */
    readonly repository?: string;
    /**
     *
     * @optional - reference repository image
     *
    */
    readonly tagOrDigest?: string;
}
