import { BuildType, EnvironmentType, PackageManagerType, RegionType } from './types';
/**
 *
 * @interface
 * @description - label properties
 *
 */
export interface LabelProps {
    readonly customer: string;
    readonly project: string;
    readonly environment: EnvironmentType;
}
/**
 *
 * @interface
 * @description - control hub properties
 *
 */
export interface ControlHubProps {
    readonly account: string;
    readonly region?: RegionType;
}
/**
 *
 * @interface
 * @description - git repository properties
 *
 */
export interface GitProps {
    /**
       *
       * @required - respository owner
       *
      */
    readonly owner: string;
    /**
       *
       * @required - respository branch name
       *
      */
    readonly branch: string;
    /**
       *
       * @required - respository name
       *
      */
    readonly repository: string;
    /**
       *
       * @required - AWS CodeStar connection id
       *
      */
    readonly codeStarId: string;
    /**
       *
       * @optional - If this is set, the next CodeBuild job clones the repository (default: false)
       *
      */
    readonly codeBuildCloneOutput?: boolean;
    /**
       *
       * @optional - controls automatically starting your pipeline when a new commit is made on the configured repository and branch (default: true)
       *
      */
    readonly triggerOnPush?: boolean;
}
/**
   *
   * @interface
   * @description - build stage properties
   *
   */
export interface BuildProps {
    /**
       *
       * @required - type of build (default: java)
       * JAVA - for java builds
       * PYTHON - for python builds
       *
      */
    readonly type: BuildType;
    /**
       *
       * PIP - python package installer
       * MAVEN - java package installer
       *
      */
    readonly packageManager?: PackageManagerType;
    /**
       *
       * @optional - customize codebuildstep commands to run
       *
      */
    readonly commands?: string[];
    /**
       *
       * @optional - customize codebuildstep installation commands
       *
      */
    readonly installCommands?: string[];
    /**
       *
       * @optional - environment variables to set
       *
      */
    readonly env?: {
        [key: string]: string;
    };
}
/**
   *
   * @interface
   * @description - publish stage properties
   *
   */
export interface PublishProps {
    /**
       *
       * @optional - customize codebuildstep commands to run
       *
      */
    readonly commands?: string[];
    /**
       *
       * @optional - customize codebuildstep installation commands
       *
      */
    readonly installCommands?: string[];
    /**
       *
       * @optional - environment variables to set
       *
      */
    readonly env?: {
        [key: string]: string;
    };
}
/**
 *
 * @interface
 * @description - notification properties
 *
 */
export interface NotificationProps {
    readonly developers?: string[];
    readonly pipelineEvents?: boolean;
    readonly stageEvents?: boolean;
}
